// components/Transactions/TransactionMenu.js

import React, { useState } from 'react';
import TransactionForm from './TransactionForm';
import TransactionList from './TransactionList';
import './TransactionMenu.css'; // Ajusta la ruta del CSS según corresponda

const TransactionMenu = () => {
  const [view, setView] = useState('list'); // 'list' o 'form'

  return (
    <div className="transaction-menu">
      <div className="menu-buttons">
        <button onClick={() => setView('list')}>Lista de Transacciones</button>
        <button onClick={() => setView('form')}>Crear Transacción</button>
      </div>
      <div className="menu-content">
        {view === 'list' ? <TransactionList /> : <TransactionForm />}
      </div>
    </div>
  );
};

export default TransactionMenu;
