// src/components/DocumentUpload.js
import React, { useState } from 'react';
import documentService from '../services/documentService'; // Ajusta la ruta si es necesario
import './DocumentUpload.css';

const DocumentUpload = () => {
  const [nombre, setNombre] = useState('');
  const [peso, setPeso] = useState('');
  const [url, setUrl] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [document, setDocument] = useState(null);

  const handleFileChange = (e) => {
    setDocument(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!nombre || !peso || !url) {
      alert('Nombre, peso y URL son campos obligatorios.');
      return;
    }

    const formData = new FormData();
    formData.append('nombre', nombre);
    formData.append('peso', Number(peso));
    formData.append('url', url);
    formData.append('descripcion', descripcion);
    formData.append('document', document);

    try {
      await documentService.uploadDocument(formData);
      alert('Documento subido con éxito.');
    } catch (error) {
      console.error('Error al subir el documento:', error);
      alert('Error al subir el documento.');
    }
  };

  return (
    <div className="document-upload">
      <h2>Subir Documento</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Peso (en números):</label>
          <input
            type="number"
            value={peso}
            onChange={(e) => setPeso(e.target.value)}
            required
          />
        </div>
        <div>
          <label>URL:</label>
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Descripción (opcional):</label>
          <textarea
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
        </div>
        <div>
          <label>Documento:</label>
          <input
            type="file"
            onChange={handleFileChange}
            required
          />
        </div>
        <button type="submit">Subir Documento</button>
      </form>
    </div>
  );
};

export default DocumentUpload;
