import React, { useState } from 'react';
import { createTransaction, updateTransaction } from '../../services/transactionService'; // Importar las funciones correctas

const TransactionForm = ({ existingTransaction, onSave }) => {
  const [description, setDescription] = useState(existingTransaction?.description || '');
  const [amount, setAmount] = useState(existingTransaction?.amount || '');
  const [date, setDate] = useState(existingTransaction?.date || '');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const transactionData = { description, amount, date };

    try {
      if (existingTransaction) {
        await updateTransaction(existingTransaction.id, transactionData);
      } else {
        await createTransaction(transactionData);
      }
      onSave();
    } catch (err) {
      setError('Error al guardar la transacción.');
      console.error('Error al guardar la transacción:', err);
    }
  };

  return (
    <div>
      <h2>{existingTransaction ? 'Editar Transacción' : 'Agregar Transacción'}</h2>
      {error && <p>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="description">Descripción:</label>
          <input
            type="text"
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="amount">Monto:</label>
          <input
            type="number"
            id="amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="date">Fecha:</label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <button type="submit">Guardar Transacción</button>
      </form>
    </div>
  );
};

export default TransactionForm;
