// src/components/Documents/DocumentList.js
import React, { useEffect, useState } from 'react';
import documentService from '../../services/documentService'; // Ajusta la ruta si es necesario
import './DocumentList.css'; // Asegúrate de que la ruta sea correcta

const DocumentList = () => {
  const [documents, setDocuments] = useState([]);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const data = await documentService.getDocuments();
        setDocuments(data);
      } catch (error) {
        console.error('Error fetching documents:', error);
      }
    };
    fetchDocuments();
  }, []);

  const handleDownload = (id) => {
    window.open(`http://localhost:3000/documents/${id}`, '_blank');
  };

  return (
    <div className="document-list">
      <h2>Lista de Documentos</h2>
      <div className="document-grid">
        {documents.length > 0 ? (
          documents.map((doc) => (
            <div key={doc._id} className="document-item">
              {doc.filePath.endsWith('.pdf') ? (
                <iframe
                  src={`http://localhost:3000/documents/${doc._id}`}
                  title={doc.nombre}
                  className="document-thumbnail"
                />
              ) : (
                <img
                  src={`http://localhost:3000/documents/${doc._id}`}
                  alt={doc.nombre}
                  className="document-thumbnail"
                />
              )}
              <p>{doc.nombre}</p>
              <button onClick={() => handleDownload(doc._id)}>Descargar</button>
            </div>
          ))
        ) : (
          <p>No se encontraron documentos.</p>
        )}
      </div>
    </div>
  );
};

export default DocumentList;
