// frontend/src/components/Transactions/TransactionForm.js
import React, { useState } from 'react';
import transactionService from '../../services/transactionService';

function TransactionForm({ onSave }) {  // Asegúrate de que onSave se pase correctamente como una prop
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const newTransaction = { description, amount };
      await transactionService.createTransaction(newTransaction);
      if (onSave) {
        onSave(); // Llama a la función onSave después de guardar la transacción
      }
    } catch (error) {
      console.error('Error al guardar la transacción:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Descripción</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Monto</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
      </div>
      <button type="submit">Guardar Transacción</button>
    </form>
  );
}

export default TransactionForm;
