// src/components/Documents/DocumentList.js

import React, { useEffect, useState } from 'react';
import { getDocuments } from '../../services/documentService';
import './DocumentList.css'; // Importa los estilos

const groupByMonth = (documents) => {
  return documents.reduce((acc, document) => {
    const month = new Date(document.fecha).toLocaleString('es-ES', { month: 'long', year: 'numeric' });
    if (!acc[month]) {
      acc[month] = [];
    }
    acc[month].push(document);
    return acc;
  }, {});
};

const DocumentList = () => {
  const [documents, setDocuments] = useState({});

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const data = await getDocuments();
        const groupedDocuments = groupByMonth(data);
        setDocuments(groupedDocuments);
      } catch (error) {
        console.error('Error al obtener los documentos:', error);
      }
    };

    fetchDocuments();
  }, []);

  return (
    <div className="document-list">
      <h1>Lista de Documentos</h1>
      {Object.keys(documents).length === 0 ? (
        <p>No hay documentos para mostrar.</p>
      ) : (
        Object.entries(documents).map(([month, documents]) => (
          <div key={month} className="document-month">
            <h2>{month}</h2>
            <ul>
              {documents.map((document) => (
                <li key={document._id} className="document-item">
                  <p><strong>Nombre:</strong> {document.nombre}</p>
                  <p><strong>Peso:</strong> {document.peso} KB</p>
                  <p><strong>Descripci√≥n:</strong> {document.descripcion || 'N/A'}</p>
                  <a href={document.url} target="_blank" rel="noopener noreferrer">
                    Ver Documento
                  </a>
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default DocumentList;
