// src/services/documentService.js
const API_URL = 'http://localhost:3000/api/documents'; // Asegúrate de que la URL base sea correcta

const uploadDocument = (formData) => {
  return fetch(`${API_URL}/upload`, { // Usa la URL base y la ruta específica para la carga
    method: 'POST',
    body: formData,
  }).then(response => response.json());
};

const getDocuments = () => {
  return fetch(API_URL) // Usa la URL base para obtener documentos
    .then(response => response.json());
};

export default { uploadDocument, getDocuments };
