// src/components/Transactions/TransactionForm.js
import React, { useState } from 'react';
import transactionService from '../../services/transactionService'; // Ajusta la ruta si es necesario
import './TransactionForm.css';

const TransactionForm = () => {
  const [nombre, setNombre] = useState('');
  const [monto, setMonto] = useState('');
  const [descripcion, setDescripcion] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!nombre || !monto) {
      alert('Nombre y monto son campos obligatorios.');
      return;
    }

    try {
      await transactionService.createTransaction({ nombre, monto, descripcion });
      alert('Transacción creada con éxito.');
    } catch (error) {
      console.error('Error al crear la transacción:', error);
      alert('Error al crear la transacción.');
    }
  };

  return (
    <div className="transaction-form">
      <h2>Crear Transacción</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Monto:</label>
          <input
            type="number"
            value={monto}
            onChange={(e) => setMonto(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Descripción (opcional):</label>
          <textarea
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
        </div>
        <button type="submit">Crear Transacción</button>
      </form>
    </div>
  );
};

export default TransactionForm;
