// src/components/Documents/DocumentList.js

import React, { useEffect, useState } from 'react';
import documentService from '../../services/documentService';
import './DocumentList.css';

const groupByMonth = (documents) => {
  return documents.reduce((acc, document) => {
    const date = new Date(document.fecha);
    const month = date.toLocaleString('es-ES', { month: 'long', year: 'numeric' });
    if (!acc[month]) {
      acc[month] = [];
    }
    acc[month].push(document);
    return acc;
  }, {});
};

const DocumentList = () => {
  const [documents, setDocuments] = useState({});
  const [openMonth, setOpenMonth] = useState(null);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const data = await documentService.getDocuments();
        const groupedDocuments = groupByMonth(data);
        setDocuments(groupedDocuments);
      } catch (error) {
        console.error('Error fetching documents:', error);
      }
    };
    fetchDocuments();
  }, []);

  const handleDownload = (id, filePath) => {
    // Asegúrate de que la URL apunte directamente al archivo para la descarga
    window.location.href = `http://localhost:3000/documents/${id}/download`;
  };

  const toggleMonth = (month) => {
    setOpenMonth(openMonth === month ? null : month);
  };

  return (
    <div className="document-list">
      <h2>Lista de Documentos</h2>
      <div className="document-months">
        {Object.keys(documents).map((month) => (
          <div key={month} className="document-month">
            <h3 onClick={() => toggleMonth(month)} className="month-header">
              {month}
              <span className={`toggle-icon ${openMonth === month ? 'open' : ''}`}>▼</span>
            </h3>
            {openMonth === month && (
              <div className="document-grid">
                {documents[month].length > 0 ? (
                  documents[month].map((doc) => (
                    <div key={doc._id} className="document-item">
                      <div className="document-thumbnail">
                        {doc.filePath.endsWith('.pdf') ? (
                          <iframe
                            src={`http://localhost:3000/documents/${doc._id}`}
                            title={doc.nombre}
                            className="pdf-viewer"
                            allow="autoplay"
                          />
                        ) : (
                          <img
                            src={`http://localhost:3000/documents/${doc._id}`}
                            alt={doc.nombre}
                            className="thumbnail-image"
                          />
                        )}
                      </div>
                      <div className="document-info">
                        <h4>{doc.nombre}</h4>
                        <p>{doc.descripcion}</p>
                        <p>{new Date(doc.fecha).toLocaleDateString('es-ES')}</p>
                        <button onClick={() => handleDownload(doc._id, doc.filePath)}>Descargar</button>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No se encontraron documentos.</p>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default DocumentList;
